import React, {useState} from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"
import {mod} from "../utils"

export default function ReleasePage() {
  const [slideIndex, setSlideIndex] = useState(0)

  function getSlideClasses(index) {
    return [classes.slide, mod(slideIndex, slides.length) === index && classes.active].join(" ")
  }

  return (
    <main>
      <h1>Releases</h1>
      <section>
        <button onClick={() => setSlideIndex(slideIndex - 1)}>Previous</button>
        {slides.map(({name, image, description}, index) => (
          <div key={index} className={getSlideClasses(index)}>
            <h3>{name}</h3>
            <img src={image} />
            <p>{description}</p>
          </div>
        ))}
        <button onClick={() => setSlideIndex(slideIndex + 1)}>Next</button>
      </section>
    </main>
  )
}
