import React from "react"
import modalStyles from "../styles/Modal.module.css"

export default function Modal({active, setActive, children}) {
  const modalClasses = [(active ? modalStyles.active : modalStyles.notActive), modalStyles.modal].join(" ")
  const modalContentClasses = [(active ? modalStyles.active : modalStyles.notActive), modalStyles.content].join(" ")
  return (
    <div
      className={modalClasses}
      onClick={() => setActive(false)}
    >
      <div
        className={active ? modalStyles.activeContent : modalStyles.notActiveContent}
        onClick={(event) => event.stopPropagation()}
      >
        {children}
      </div>
    </div>
  )
}
