  const inputForm = useRef(null)

  return (
    <div className={classes.window}>
      <div className={classes.frame}></div>
      <form ref={inputForm}>
        <p>
          guest<em>$</em> <span style={colors.foam}>Your name: </span>
          <input
            autoComplete="new-password"
            onKeyDown={(event) => {
              if (event.key === "Enter") {
                console.log(inputForm.current[1])
              }
            }}
          />
        </p>
        <p>
          guest<em>$</em> <span style={colors.foam}>Your phone: </span>
          <input autoComplete="new-password" />
        </p>
      </form>
    </div>
  )
}
