  const formRef = useRef(null)

  const lines = [
    {prompt: "Your name: "},
    {prompt: "Your phone: "},
    {prompt: "Your email adress: "},
    {prompt: "Your adress: "},
  ]

  return (
    <div className={classes.window}>
      <div className={classes.frame}></div>
      <form ref={formRef}>
        =>(
        <p>
          guest<em>$</em> <span style={colors.foam}>Your name: </span>
          <input
            autoComplete="new-password"
            onKeyDown={(event) => {
              if (event.key === "Enter") {
                formRef.current[1].focus()
              }
            }}
            />
        </p>
        )
      </form>
    </div>
  )
}
