import React, { useState } from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"

export default function ReleasePage() {
  const slideIndex = useState(0)

  return (
    <main>
      <h1>Releases</h1>
      <section>
        {slides.map(({name, image, description}, index) => 
        { slideIndex === index &&
            <div>
              <h3>{name}</h3>
              <img src={image} />
              <p>{description}</p>
            </div>
          }
        )}
      </section>
    </main>
  )
}
