import React from "react"
import classes from "../styles/Modal.module.css"

export default function Modal({active, setActive, children}) {
  const activeClass = active ? classes.active : classes.notActive
  const overlayClasses = [activeClass, classes.modal].join(" ")
  const contentClasses = [activeClass, classes.content].join(" ")

  return (
    <div className={overlayClasses} onClick={() => setActive(false)}>
      <div className={contentClasses} onClick={(event) => event.stopPropagation()}>
        {children}
      </div>
    </div>
  )
}
