import React, {useState} from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"
import {mod} from "../utils"

export default function ReleasePage() {
  const [slideIndex, setSlideIndex] = useState(2)

  function getSlideStyles(index) {
    if (index < slideIndex) return {left: "-100%"}
    else if (index === slideIndex) return {left: 0, transform: "scale(1)", transition: "0.5s"}
    else return {left: "100%", transform: "scale(0.5)"}
  }

  return (
    <main>
      <h1>Releases</h1>
      <button onClick={() => setSlideIndex(mod(slideIndex - 1, slides.length))}>
        Previous
      </button>
      <button onClick={() => setSlideIndex(mod(slideIndex + 1, slides.length))}>
        Next
      </button>
      <section className={classes.slideSet}>
        {slides.map(({name, image, description}, index) => (
          <div key={index} style={getSlideStyles(index)}>
            <h3>{name}</h3>
            <p>{description}</p>
          </div>
        ))}
      </section>
    </main>
  )
}
