import React, {useState} from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"

export default function ReleasePage() {
  const [slideIndex, setSlideIndex] = useState(0)

  next
  return (
    <main>
      <h1>Releases</h1>
      <section>
        <button onClick={() => setSlideIndex()}>Previous</button>
        {slides.map(({name, image, description}, index) => (
          <div key={index} {...(slideIndex !== index && {className: classes.notActive})}>
            <h3>{name}</h3>
            <img src={image} />
            <p>{description}</p>
          </div>
        ))}
        <button>Next</button>
      </section>
    </main>
  )
}
