import React, {useState} from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"
import {mod} from "../utils"

export default function ReleasePage() {
  const [slideIndex, setSlideIndex] = useState(2)

  function getSlideStyles(index) {
    if (index < slideIndex) return {left: "-100%"}
    else if (index === slideIndex) return {left: 0}
    else return {left: "100%"}
  }

  return (
    <main>
      <h1>Releases</h1>
      <div className={classes.slider}>
        <button onClick={() => setSlideIndex(mod(slideIndex - 1, slides.length))}>Previous</button>
        <div className={classes.slideSet}>
          {slides.map(({name, image, description}, index) => (
            <div key={index} style={getSlideStyles(index)}>
              <h3>{name}</h3>
              <img src={image} />
              <p>{description}</p>
            </div>
          ))}
        </div>
        <button onClick={() => setSlideIndex(mod(slideIndex + 1, slides.length))}>Next</button>
      </div>
      <div>
        {slides.map(({}, index) => (
          <div key={index}>
          </div>
        ))}
      </div>
    </main>
  )
}
