import React from "react"
import modalStyles from "../styles/Modal.module.css"

export default function Modal({active, setActive, children}) {
  const activeClass = active ? modalStyles.active : modalStyles.notActive
  const modalClasses = [
    activeClass,
    modalStyles.modal,
  ].join(" ")
  const modalContentClasses = [
    activeClass,
    modalStyles.content,
  ].join(" ")

  return (
    <div className={modalClasses} onClick={() => setActive(false)}>
      <div className={modalContentClasses} onClick={(event) => event.stopPropagation()}>
        {children}
      </div>
    </div>
  )
}
