import React, {useState} from "react"
import {slides} from "../consts"
import classes from "../styles/ReleasePage.module.css"
import {mod} from "../utils"

export default function ReleasePage() {
  const [slideIndex, setSlideIndex] = useState(0)

  function isActive(index) {
    return mod(slideIndex, slides.length) === index
  }

  function getSlideClasses(index) {
    return [classes.slide, isActive(index) && classes.active].join(" ")
  }

  return (
    <main>
      <h1>Releases</h1>
      <section className={classes.slideSet}>
        {slides.map(({name, image, description}, index) => (
          <div
            key={index}
            className={getSlideClasses(index)}
          >
            <h3>{name}</h3>
            <p>{description}</p>
          </div>
        ))}
      </section>
    </main>
  )
}
